import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Card, 
  CardContent, 
  Typography, 
  FormControl,
  Select,
  MenuItem,
  InputLabel,
  Stack,
  TextField,
  ToggleButtonGroup,
  ToggleButton,
  FormControlLabel,
  Switch
} from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import { Line, Bar } from 'react-chartjs-2';
import { useScenariosContext } from '../contexts/ScenariosContext';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

const CostChart = () => {
  const { scenarios, calculatedData, monthRange, updateMonthRange } = useScenariosContext();
  const [chartType, setChartType] = useState('line');
  const [dataType, setDataType] = useState('monthly');
  const [costBreakdown, setCostBreakdown] = useState(false);

  const handleChartTypeChange = (_, newType) => {
    if (newType !== null) {
      setChartType(newType);
    }
  };

  const handleDataTypeChange = (_, newType) => {
    if (newType !== null) {
      setDataType(newType);
    }
  };

  const handleMonthRangeChange = (event) => {
    updateMonthRange(Number(event.target.value));
  };

  const getChartData = () => {
    const labels = Array.from({ length: monthRange }, (_, i) => `Month ${i + 1}`);

    // Filter visible scenarios
    const visibleScenarios = scenarios.filter(s => s.visible !== false);

    if (costBreakdown && visibleScenarios.length === 1) {
      // Single scenario cost breakdown
      const scenario = visibleScenarios[0];
      const scenarioData = calculatedData[scenario.id];

      if (!scenarioData) return { labels: [], datasets: [] };

      return {
        labels,
        datasets: [
          {
            label: 'Storage Costs',
            data: scenarioData.monthlyData.storageCosts,
            backgroundColor: 'rgba(54, 162, 235, 0.6)',
            borderColor: 'rgba(54, 162, 235, 1)',
            borderWidth: 1,
          },
          {
            label: 'Transaction Costs',
            data: scenarioData.monthlyData.transactionCosts,
            backgroundColor: 'rgba(255, 99, 132, 0.6)',
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWidth: 1,
          },
          {
            label: 'Outbound Transfer Costs',
            data: scenarioData.monthlyData.outboundCosts,
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
          },
        ],
      };
    } else {
      // Multiple scenarios comparison
      const datasets = visibleScenarios.map(scenario => {
        const scenarioData = calculatedData[scenario.id];
        if (!scenarioData) return null;

        return {
          label: scenario.name,
          data: dataType === 'cumulative' 
            ? scenarioData.cumulativeTotalCosts 
            : scenarioData.monthlyData.totalCosts,
          borderColor: scenario.color,
          backgroundColor: `${scenario.color}33`, // Add transparency
          borderWidth: scenario.active ? 3 : 2,
          pointRadius: scenario.active ? 4 : 3,
          fill: chartType === 'area',
          tension: 0.2,
        };
      }).filter(Boolean);

      return {
        labels,
        datasets,
      };
    }
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Cost (EUR)',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Month',
        },
      },
    },
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: costBreakdown 
          ? 'Cost Breakdown by Category'
          : (dataType === 'cumulative' 
              ? 'Cumulative Storage Costs Over Time' 
              : 'Monthly Storage Costs Over Time'),
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            return `${context.dataset.label}: €${context.parsed.y.toFixed(2)}`;
          }
        }
      }
    },
  };

  // Prepare the chart data
  const chartData = getChartData();

  return (
    <Card elevation={3}>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h5" component="h2">
            Cost Visualization
          </Typography>

          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
            <FormControl size="small" sx={{ minWidth: 120 }}>
              <InputLabel id="month-range-label">Months</InputLabel>
              <Select
                labelId="month-range-label"
                id="month-range-select"
                value={monthRange}
                label="Months"
                onChange={handleMonthRangeChange}
              >
                <MenuItem value={12}>12 Months</MenuItem>
                <MenuItem value={24}>24 Months</MenuItem>
                <MenuItem value={36}>36 Months</MenuItem>
                <MenuItem value={48}>48 Months</MenuItem>
                <MenuItem value={60}>60 Months</MenuItem>
              </Select>
            </FormControl>

            <ToggleButtonGroup
              value={chartType}
              exclusive
              onChange={handleChartTypeChange}
              size="small"
            >
              <ToggleButton value="line">Line</ToggleButton>
              <ToggleButton value="area">Area</ToggleButton>
              <ToggleButton value="bar">Bar</ToggleButton>
            </ToggleButtonGroup>

            <ToggleButtonGroup
              value={dataType}
              exclusive
              onChange={handleDataTypeChange}
              size="small"
            >
              <ToggleButton value="monthly">Monthly</ToggleButton>
              <ToggleButton value="cumulative">Cumulative</ToggleButton>
            </ToggleButtonGroup>
          </Stack>
        </Box>

        {visibleScenarios.length === 1 && (
          <FormControlLabel
            control={
              <Switch 
                checked={costBreakdown} 
                onChange={() => setCostBreakdown(!costBreakdown)} 
              />
            }
            label="Show Cost Breakdown"
            sx={{ mb: 2 }}
          />
        )}

        <Box className="chart-container" sx={{ height: 400 }}>
          {chartType === 'bar' ? (
            <Bar data={chartData} options={chartOptions} />
          ) : (
            <Line data={chartData} options={chartOptions} />
          )}
        </Box>
      </CardContent>
    </Card>
  );
};

export default CostChart;
