import React, { useState } from 'react';
import { ThemeProvider, CssBaseline, Container, Box, Typography } from '@mui/material';
import theme from './styles/theme';
import ScenarioManager from './components/ScenarioManager';
import CostChart from './components/CostChart';
import ScenarioList from './components/ScenarioList';
import { ScenariosProvider } from './contexts/ScenariosContext';
import './styles/App.css';

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ScenariosProvider>
        <Container maxWidth="lg">
          <Box sx={{ my: 4 }}>
            <Typography variant="h3" component="h1" gutterBottom>
              Azure Blob Storage Cost Calculator
            </Typography>
            <Typography variant="subtitle1" gutterBottom>
              Calculate and compare Azure Blob Storage costs for different scenarios over time
            </Typography>

            <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 3, mt: 4 }}>
              <Box sx={{ flex: 1 }}>
                <ScenarioManager />
                <ScenarioList />
              </Box>
              <Box sx={{ flex: 2 }}>
                <CostChart />
              </Box>
            </Box>
          </Box>
        </Container>
      </ScenariosProvider>
    </ThemeProvider>
  );
}

export default App;
