import React from 'react';
import { 
  Box, 
  Card, 
  CardContent, 
  Typography, 
  List,
  ListItem,
  ListItemText,
  IconButton,
  Chip,
  Divider,
  Stack,
  Switch,
  FormControlLabel
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import { useScenariosContext } from '../contexts/ScenariosContext';

const ScenarioList = () => {
  const { 
    scenarios, 
    deleteScenario, 
    setActiveScenario, 
    toggleScenarioVisibility 
  } = useScenariosContext();

  if (scenarios.length === 0) {
    return null;
  }

  return (
    <Card elevation={3} sx={{ mb: 4 }}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom>
          Scenarios
        </Typography>

        <List>
          {scenarios.map((scenario, index) => (
            <React.Fragment key={scenario.id}>
              {index > 0 && <Divider component="li" />}
              <ListItem 
                alignItems="flex-start"
                className={scenario.active ? 'active-scenario' : ''}
                sx={{
                  pl: scenario.active ? 2 : 3,
                  borderLeft: scenario.active ? `4px solid ${scenario.color}` : 'none',
                  bgcolor: scenario.active ? 'rgba(0, 0, 0, 0.03)' : 'transparent'
                }}
                secondaryAction={
                  <Stack direction="row" spacing={1}>
                    <IconButton 
                      edge="end" 
                      aria-label="toggle-visibility"
                      onClick={() => toggleScenarioVisibility(scenario.id)}
                    >
                      {scenario.visible === false ? <VisibilityOffIcon /> : <VisibilityIcon />}
                    </IconButton>
                    <IconButton 
                      edge="end" 
                      aria-label="delete"
                      onClick={() => deleteScenario(scenario.id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Stack>
                }
              >
                <ListItemText
                  primary={
                    <Box 
                      sx={{ 
                        display: 'flex', 
                        alignItems: 'center',
                        cursor: 'pointer'
                      }}
                      onClick={() => setActiveScenario(scenario.id)}
                    >
                      <Box 
                        sx={{ 
                          width: 16, 
                          height: 16, 
                          borderRadius: '50%', 
                          bgcolor: scenario.color,
                          mr: 1
                        }} 
                      />
                      <Typography variant="subtitle1" component="span">
                        {scenario.name}
                      </Typography>
                    </Box>
                  }
                  secondary={
                    <React.Fragment>
                      <Typography
                        sx={{ display: 'block' }}
                        component="span"
                        variant="body2"
                        color="text.primary"
                      >
                        {scenario.description}
                      </Typography>
                      <Stack direction="row" spacing={1} sx={{ mt: 1 }}>
                        <Chip 
                          size="small" 
                          label={`${scenario.parameters.numUsers} users`} 
                        />
                        <Chip 
                          size="small" 
                          label={`${scenario.parameters.projectsPerUserPerMonth} projects/month`} 
                        />
                        <Chip 
                          size="small" 
                          label={`${scenario.parameters.imagesPerProject} images/project`} 
                        />
                      </Stack>
                    </React.Fragment>
                  }
                />
              </ListItem>
            </React.Fragment>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default ScenarioList;
